import React, { Component } from "react";
import "./style.css";
import Layout from "../layout/Layout";
import { useCallback } from "react";


function Form({todoList, setTodoList}){

    const title = React.useRef();
    const content = React.useRef();

    const add =()=> {
         const titleValue = title.current.value
         const contentValue = content.current.value

         const todoData = {
            titleValue:titleValue, 
            contentValue:contentValue,
            finsh:false
         }
         const newTodoList = [...todoList, todoData];
         setTodoList(newTodoList)
         console.log(todoList);
    }
    return (
        <div className="InputForm">
            <div className="input-group">
                <input className="input1" type="text" ref={title}/>
                <input className="input2" type="text" ref={content}></input>
                <button type="button" onClick={add}>추가하기</button>
            </div>
        </div> 
    );
}

export default Form;
