import React, { useState ,useRef} from "react";
import "./style.css";
import {createtodos} from "../../redux/modules/todos";
import {useDispatch} from "react-redux";
import nextId from "react-id-generator";


function Form({ setTodos, todos }) {
  const id = nextId();
  const dispatch = useDispatch();
  // const initialState = {
  //   id: 0,  // 1도될듯
  //   title: "",
  //   body: "",
  //   isDone: false,

  // };


  const [todo, setTodo] = useState({
    id: 0,
    title: "",
    body: "",
    isDone: false,
  });

  // const [todo, setTodo] = useState(initialState);

  const onChangeHandler = (event) => {
    const { name, value } = event.target; //속성


    setTodo({ ...todo, [name]: value}); 
    // 스프레드하면 id.title등으로 분리 



  };

  const onSubmitHandler = (event) => {
    event.preventDefault();  //초기화
    if (todo.title.trim() === "" || todo.body.trim() === "") return; // trim 선행 후행 공백제거 굳이? 리턴은왜?

    dispatch(createtodos({ ...todo, id}));
    // setTodos([...todos, ]);
    // setTodo(initialState);



    setTodo({
      id: 0,
      title: "",
      body: "",
      isDone: false,
    });


  };



  return (
    <form onSubmit={onSubmitHandler} className="add-form">
      <div className="input-group">
        <label className="form-label">제목</label>
        <input

          type="text"
          name="title"
          value={todo.title}
          className="add-input input-body"
          onChange={onChangeHandler}


        />

        <label className="form-label">내용</label>
        <input
          type="text"
          name="body"
          value={todo.body}
          className="add-input"
          onChange={onChangeHandler}  //
        />
      </div>
      <button  className="add-button">추가하기</button>
    </form>
  );
}

export default Form;
